import React from 'react'
import { useSelector } from 'react-redux'
import { Box, Grid } from '@mui/material'
//import CardItem from './CardItem'
import { Card, CardContent, Typography } from '@mui/material'
import { useHistory } from 'react-router-dom'

const CardItem = (props) => {
  let ship = props.ship
  const history = useHistory()

  const handleClick = (id) => {
    history.push(`/ship/${id}`)
  } 

  const getShipCountry = (shipOwner) => {
    if(!shipOwner.includes('(')) return;
    else { 
      const middle = shipOwner.slice(
        shipOwner.indexOf('(') + 1,
        shipOwner.lastIndexOf(')'),
      );
      return middle;
    }
  }

  return (
    <Card key={ship.id} elevation={5} onClick={() => handleClick(ship.id)}>
      <React.Fragment>
        <CardContent>
          <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
            Ship #{ship.id}
          </Typography>
          <Typography variant="h5" component="div">
            {ship.name}
          </Typography>
          <Typography sx={{ mb: 1.5 }} color="text.secondary">
            Country of origin: {getShipCountry(ship.owner)}
          </Typography>
          <Typography variant="body2">
            TEU: {ship.maxTEU}
          </Typography>
        </CardContent>
      </React.Fragment>
    </Card>
  )
}

const Ships = () => {
  const ships = useSelector(state => state.ships)

  const gridStyle = {
    padding: 30,
  }

  return (
    <Box sx={{ flexGrow: 1 }}>
      <Grid container spacing={3} style={gridStyle}>
        {ships.map(ship =>
        <Grid item xs={4} s={4}>
          <CardItem ship={ship}/>
        </Grid>
        )}
      </Grid>
    </Box>
  )
}

export default Ships
